graphql.query:
query($skus: [String!]) {
    products(skus: $skus) {
       __typename
        id
        sku
        name
        description
        shortDescription
        metaDescription
        metaKeyword
        metaTitle
        addToCartAllowed
        inStock
        lowStock
        url
        images(roles: []) {
            url
            label
            roles
        }
        attributes(roles: []) {
            name
            label
            value
            roles
        }
        ... on SimpleProductView {
            price {
                final {
                    amount {
                        value
                        currency
                    }
                }
                regular {
                    amount {
                        value
                        currency
                    }
                }
                roles 
            }
        }
        ... on ComplexProductView {
            options {
                id
                title
                required
                multi
                values {
                    id
                    title 
                    ... on ProductViewOptionValueProduct {
                        title,
                        quantity,
                        isDefault,
                        product {
                            sku
                            shortDescription
                            name
                            price {
                                final {
                                    amount {
                                        value
                                        currency
                                    }
                                }
                                regular {
                                    amount {
                                        value
                                        currency
                                    }
                                }
                                roles
                            }
                        }
                    }
                    ... on ProductViewOptionValueSwatch {
                        id 
                        title
                        type
                        value
                    }
                }
            }
            priceRange {
                maximum {
                    final {
                        amount {
                            value
                            currency
                        }
                    }
                    regular {
                        amount {
                            value
                            currency
                        }
                    }
                    roles
                }
                minimum {
                    final {
                        amount {
                            value
                            currency
                        }
                    }
                    regular {
                        amount {
                            value
                            currency
                        }
                    }
                    roles
                }
            }
        }
    }
}

query($phrase:String!) {
    productSearch(phrase:$phrase){
        total_count
        suggestions
        related_terms
        page_info {
            current_page
            page_size
            total_pages
        }
        facets {
            title
            attribute
            type
            buckets {
                title
            }
        }
        items {
            productView {
                description
                id
                metaDescription
                metaKeyword
                metaTitle
                shortDescription
                addToCartAllowed
                name
                sku
                ...on SimpleProductView {
                    price {
                        final {
                            amount {
                                value
                                currency
                            }
                        }
                        regular {
                            amount {
                                value
                                currency
                            }
                        }
                        roles
                    }
                }
                ...on ComplexProductView {
                    options {
                        id
                        title
                        required
                        values {
                            id
                            title
                            ... on ProductViewOptionValueProduct {
                                title,
                                quantity,
                                isDefault,
                                product {
                                    sku
                                    shortDescription
                                    name
                                    price {
                                        final {
                                            amount {
                                                value
                                                currency
                                            }
                                        }
                                        regular {
                                            amount {
                                                value
                                                currency
                                            }
                                        }
                                        roles
                                    }
                                }
                            }                             
                        }
                    }
                    priceRange {
                        maximum {
                            final {
                                amount {
                                    value
                                    currency
                                }
                            }
                            regular {
                                amount {
                                    value
                                    currency
                                }
                            }
                            roles
                        }
                        minimum {
                            final {
                                amount {
                                    value
                                    currency
                                }
                            }
                            regular {
                                amount {
                                    value
                                    currency
                                }
                            }
                            roles
                        }
                    }
                }
            }
            highlights {
                attribute
                value
                matched_words
            }
            applied_query_rule {
                rule_id
                rule_name
                action_type
            }
        }
    }
}

